@import url("https://fonts.googleapis.com/css?family=Roboto:300,400,700");
body {
  padding: 0;
  margin: 0;
  font-family: "Roboto", sans-serif;
  color: #000;
  background: #fff;
}

.page {
  padding: 16px;
}
.page .page-title {
  margin: 16px 0;
  font-size: 17px;
  font-weight: 300;
  color: #000;
}
@media screen and (min-width: 0) {
  .page .page-title {
    font-size: 17px;
  }
}
@media screen and (min-width: 960px) {
  .page .page-title {
    font-size: 21px;
  }
}
@media screen and (min-width: 1280px) {
  .page .page-title {
    font-size: 34px;
  }
}
.page .page-subtitle {
  margin: 16px 0;
  font-size: 10px;
  font-weight: 400;
  color: #828282;
}
@media screen and (min-width: 0) {
  .page .page-subtitle {
    font-size: 10px;
  }
}
@media screen and (min-width: 960px) {
  .page .page-subtitle {
    font-size: 10px;
  }
}
@media screen and (min-width: 1280px) {
  .page .page-subtitle {
    font-size: 11px;
  }
}
.page .btn {
  display: inline-block;
  border: none;
  min-width: 120px;
  padding: 12px 32px;
  margin: 0;
  text-decoration: none;
  background: #000;
  color: #fff;
  font-family: "Roboto", sans-serif;
  font-size: 11px;
  font-weight: 700;
  border-radius: 8px;
  line-height: 1;
  cursor: pointer;
  text-align: center;
  transition: all 250ms ease-in-out;
  -webkit-appearance: none;
  -moz-appearance: none;
  outline: none;
}
@media screen and (min-width: 0) {
  .page .btn {
    font-size: 11px;
  }
}
@media screen and (min-width: 960px) {
  .page .btn {
    font-size: 11px;
  }
}
@media screen and (min-width: 1280px) {
  .page .btn {
    font-size: 13px;
  }
}
.page .btn:hover {
  background: #1a1a1a;
}
.page .btn:active {
  transform: scale(0.99);
}
.page .btn.btn-primary {
  background: #FFCD00;
  color: #000;
}
.page .btn.btn-primary:hover {
  background: #ffd733;
}
.page .page-content {
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
}

.component {
  position: relative;
}

.header {
  text-align: center;
}
.header .header-logo {
  display: inline-block;
  padding: 16px 0;
}

.component-offers .offers-list {
  margin: 16px 0 24px 0;
  padding: 0;
  list-style: none;
}
.component-offers .offers-list__action .offer-item {
  display: block;
}
@media only screen and (min-width: 600px) {
  .component-offers .offers-list__action .offer-item {
    display: flex;
  }
}
.component-offers .offers-list__action .offer-item .details__has-action {
  justify-content: space-between;
  border-top: 1px solid #E8E8E8;
  padding-top: 16px;
  margin-top: 16px;
}
@media only screen and (min-width: 600px) {
  .component-offers .offers-list__action .offer-item .details__has-action {
    display: flex;
    justify-content: unset;
    border-top: none;
    padding-top: 0;
    margin-top: 0;
  }
}

.offer-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 8px;
  border: 1px solid #E8E8E8;
  border-top-color: transparent;
  transition: all 250ms ease-in-out;
}
@media only screen and (min-width: 600px) {
  .offer-item {
    padding: 8px 16px;
  }
}
.offer-item:first-child {
  border-top-color: #E8E8E8;
}
.offer-item:hover {
  border-color: #9DCDFA;
  background: #FBFCFF;
}
.offer-item .details {
  display: flex;
  align-items: flex-start;
}
.offer-item .offer-item__status .status {
  width: 12px;
  height: 12px;
  background: #000;
  display: block;
  border-radius: 50%;
  margin-right: 10px;
}
.offer-item .offer-item__status .status.status__success {
  background: #44C600;
}
.offer-item .offer-item__status .status.status__alert {
  background: #FF4141;
}
.offer-item .offer-item__company {
  margin: 0 0 5px 0;
  font-weight: 400;
  font-size: 11px;
}
@media screen and (min-width: 0) {
  .offer-item .offer-item__company {
    font-size: 11px;
  }
}
@media screen and (min-width: 960px) {
  .offer-item .offer-item__company {
    font-size: 11px;
  }
}
@media screen and (min-width: 1280px) {
  .offer-item .offer-item__company {
    font-size: 13px;
  }
}
.offer-item .offer-item__name {
  margin: 0;
  font-size: 11px;
  color: #828282;
  font-weight: 300;
  font-size: 10px;
}
@media screen and (min-width: 0) {
  .offer-item .offer-item__name {
    font-size: 10px;
  }
}
@media screen and (min-width: 960px) {
  .offer-item .offer-item__name {
    font-size: 10px;
  }
}
@media screen and (min-width: 1280px) {
  .offer-item .offer-item__name {
    font-size: 11px;
  }
}
.offer-item .offer-item__price {
  align-self: center;
  font-size: 11px;
  font-weight: 700;
}
@media screen and (min-width: 0) {
  .offer-item .offer-item__price {
    font-size: 11px;
  }
}
@media screen and (min-width: 960px) {
  .offer-item .offer-item__price {
    font-size: 11px;
  }
}
@media screen and (min-width: 1280px) {
  .offer-item .offer-item__price {
    font-size: 13px;
  }
}
.offer-item .offer-item__action {
  margin-left: 24px;
}

.loader {
  display: block;
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  background: rgba(255, 255, 255, 0.5);
}
.loader .loader-inner {
  display: inline-block;
  width: 64px;
  height: 64px;
  position: relative;
  top: 100px;
  left: 50%;
  transform: translateX(-50%);
}
.loader .loader-inner div {
  position: absolute;
  border: 4px solid #000;
  opacity: 1;
  border-radius: 50%;
  animation: ripple 1s cubic-bezier(0, 0.2, 0.8, 1) infinite;
}
.loader .loader-inner div:nth-child(2) {
  animation-delay: -0.5s;
}

@keyframes ripple {
  0% {
    top: 28px;
    left: 28px;
    width: 0;
    height: 0;
    opacity: 1;
  }
  100% {
    top: -1px;
    left: -1px;
    width: 58px;
    height: 58px;
    opacity: 0;
  }
}
